function switchWindow(e){e.classList.toggle("delivery__content-wrapper--opened")}function findToggleWindow(e){let t=e.parentElement.children;for(let e=0;e<t.length;e++)if(t[e].classList.contains("delivery__content-wrapper")){switchWindow(t[e]);break}}function buttonAction(e){e.classList.toggle("delivery__toggle-button--opened"),findToggleWindow(e)}let testButton=document.querySelector(".delivery__toggle-button");