let specWindow=document.querySelector(".specification"),deliveryWindow=document.querySelector(".delivery"),descriptionWindow=document.querySelector(".description");function toggleAllButtons(){let e=document.querySelectorAll(".toggle__button");for(let o=0;o<e.length;o++)e[o].classList.remove("toggle__button--opened")}function closeAllWindows(){specWindow.classList.remove("specification--opened"),deliveryWindow.classList.remove("delivery--opened"),descriptionWindow.classList.remove("description--opened")}function reactToEvent(e){toggleAllButtons(),e.classList.add("toggle__button--opened"),e.classList.contains("toggle__button--specification")?(console.log("Характеристики!"),closeAllWindows(),specWindow.classList.add("specification--opened")):e.classList.contains("toggle__button--delivery")?(console.log("Доставка!"),closeAllWindows(),deliveryWindow.classList.add("delivery--opened")):e.classList.contains("toggle__button--description")&&(console.log("Описание!"),closeAllWindows(),descriptionWindow.classList.add("description--opened"))}